wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(3061905871289067676)
,p_process_sequence=>10
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'PROCESS_FILE'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'-- promo',
'-- *inserting* data into FRC_STORE_ORDER_FILE_LIST and ACP_ORDER_ITEM_FILES_DATA',
'-- *updating* data into FRC_STORE_ORDER_SKU',
'--',
'DECLARE ',
'  vFILE_ID                    VARCHAR2(25)  := NULL;',
'  vERROR_MESSAGE              VARCHAR2(2000):= NULL;',
'  vFILE_RECORD                VARCHAR2(2000):= NULL;',
'  vSTORE_AD_FILE              VARCHAR2(25)  := NULL;',
'  --',
'  nOrder_Quantity             VARCHAR2(10)  := NULL;',
'  vComments                   VARCHAR2(255) := NULL;',
'  vWH_ITEM_IND                VARCHAR2(25)  := NULL;',
'  nORDER_SOURCE               NUMBER        := NULL;',
'  vORDER_SOURCE_DESCRIPTION   VARCHAR2(120) := NULL;',
'  --',
'  vBOOKMARK                   NUMBER(3):=0;',
'BEGIN',
'    -- insert into ACC_E_COMM_LOG(msg) values(''PROMO File process 0 > ''||:P5_FILE_BROWSE);',
'    -- commit;',
'   vFILE_ID := FRC_STORE_ORDER_FILE_LIST_SEQ.NEXTVAL;',
'   :P5_FILE_ID := vFILE_ID;',
'   --',
'   IF :P5_FILE_BROWSE IS NOT NULL THEN',
'      --',
'      INSERT',
'        INTO FRC_STORE_ORDER_FILE_LIST ( FILE_ID,',
'                                          FILE_NAME,',
'                                          CREATED_BY,',
'                                          CREATED_DTM )',
'                                 VALUES ( vFILE_ID,',
'                                          substr( :P5_FILE_BROWSE, instr( :P5_FILE_BROWSE, ''/'', -1)+1),',
'                                          V(''APP_USER''),',
'                                          SYSDATE );',
'      vBOOKMARK := 5;',
'-- ',
'      FOR X IN (  select  col001  AS SKU                     ,',
'                          col002  AS UPC                        ,',
'                          col003  AS DESCRIPTION                          ,',
'                          col004  AS CASE_PACK                          ,',
'                          col005  AS FORECASTED_DEMAND             ,',
'                          col006  AS BOH                    ,',
'                          col007  AS SOQ      ,',
'                          col008  AS SOQ_ADJUSTED        ,',
'                          col009  AS SOQ_CASES               ,',
'                          col010  AS WH_ON_HAND          ,',
'                          col011  AS STORE_CURRENT_AVAIL        ,',
'                          col012  AS STORE_FUTURE_AVAIL',
'                    from apex_application_temp_files f, ',
'                         table( apex_data_parser.parse(',
'                                p_content                     => f.blob_content,',
'                                p_add_headers_row             => ''Y'',',
'                                p_max_rows                    => 50000,',
'                                p_store_profile_to_collection => ''FILE_PARSER_COLLECTION'',',
'                                p_file_name                   => f.filename ) ) p',
'                   where name = :P5_FILE_BROWSE',
'                     and line_number > 1 )',
'      LOOP',
'          --',
'           vBOOKMARK := 10;',
'           vFILE_RECORD := null; ',
'           vFILE_RECORD := ''SKU => ''||SUBSTR(LTRIM(RTRIM(X.SKU)),1,6)||'' UPC => ''||SUBSTR(LTRIM(RTRIM(X.UPC)),1,13);',
'           --',
'--           nOrder_Quantity := SUBSTR(LTRIM(RTRIM(X.Order_Quantity)),1,10);',
'--           vComments:= SUBSTR(LTRIM(RTRIM(X.Comments)),1,255);',
'           --',
'           vBOOKMARK := 13;',
'           INSERT INTO FRC_STORE_ORDER_FILE_DATA',
'            (  ',
'                FILE_ID, ',
'                BATCH_ID, ',
'                BUSINESS_UNIT, ',
'                STORE,',
'                SUPPLIER,',
'                SKU,',
'                UPC,',
'                DESCRIPTION,',
'                CASE_PACK,',
'                FORECASTED_DEMAND, ',
'	            BOH, ',
'	            SOQ, ',
'	            SOQ_ADJUSTED, ',
'	            SOQ_CASES, ',
'	            WH_ON_HAND,',
'	            STORE_CURRENT_AVAIL,',
'	            STORE_FUTURE_AVAIL,',
'                CREATED_BY, ',
'                CREATED_DTM',
'            )',
'            VALUES',
'            (',
'                vFILE_ID,                   ',
'                UPPER(SUBSTR(LTRIM(RTRIM(X.BATCH_ID)),1,20)),  --Promo_ID',
'                UPPER(SUBSTR(LTRIM(RTRIM(:P5_BUSINESS_UNIT_FILTER)),1,25)),',
'                SUBSTR(LTRIM(RTRIM(:P5_STORE_FILTER)),1,4),',
'                SUBSTR(LTRIM(RTRIM(:P5_SUPPLIER_FILTER)),1,10),',
'                SUBSTR(LTRIM(RTRIM(X.SKU)),1,8),',
'                SUBSTR(LTRIM(RTRIM(X.UPC)),1,13),',
'                SUBSTR(LTRIM(RTRIM(X.DESCRIPTION)),1,100),',
'                TO_CHAR(TRUNC(SUBSTR(LTRIM(RTRIM(X.CASE_PACK)),1,10),2),''99999.99''),',
'                TO_CHAR(TRUNC(SUBSTR(LTRIM(RTRIM(X.FORECASTED_DEMAND)),1,10),2),''99999.99''),',
'                TO_CHAR(TRUNC(SUBSTR(LTRIM(RTRIM(X.BOH)),1,10),2),''99999.99''),',
'                TO_CHAR(TRUNC(SUBSTR(LTRIM(RTRIM(X.SOQ)),1,10),2),''99999.99''),',
'                TO_CHAR(TRUNC(SUBSTR(LTRIM(RTRIM(X.SOQ_ADJUSTED)),1,10),2),''99999.99''),',
'                TO_CHAR(TRUNC(SUBSTR(LTRIM(RTRIM(X.SOQ_CASES)),1,10),2),''99999.99''),',
'                TO_CHAR(TRUNC(SUBSTR(LTRIM(RTRIM(X.WH_ON_HAND)),1,10),2),''99999.99''),',
'                TO_CHAR(TRUNC(SUBSTR(LTRIM(RTRIM(X.STORE_CURRENT_AVAIL)),1,10),2),''99999.99''),',
'                TO_CHAR(TRUNC(SUBSTR(LTRIM(RTRIM(X.STORE_FUTURE_AVAIL)),1,10),2),''99999.99''),',
'                V(''APP_USER''),',
'                SYSDATE ',
'            );',
'             --',
'          END LOOP;',
'      --',
'      vBOOKMARK := 15;',
'      UPDATE FRC_STORE_ORDER_FILE_DATA ',
'         SET UPC = LPAD(UPC,13,0) ',
'       WHERE FILE_ID = vFILE_ID',
'         AND LENGTH(UPC) < 13',
'         AND UPC IS NOT NULL;',
'      --',
'      --',
'     ',
'',
'      --',
'      --',
'      vBOOKMARK := 20;',
'      IF vSTORE_AD_FILE = ''N'' THEN ',
'         UPDATE FRC_STORE_ORDER_FILE_DATA',
'            SET ERROR = ''Error: Invalid HQ Promo ID and Store# Event.''',
'          WHERE FILE_ID = vFILE_ID',
'           AND (EVENT_ID, STORE) NOT IN ( SELECT EVENT_ID, STORE FROM ACP_ORDER_ITEMS_HDR );',
'      END IF;',
'',
'',
'      --',
'      -- Store provided AD event id must start with ''AD_''',
'      --',
'      vBOOKMARK := 25;',
'      IF vSTORE_AD_FILE = ''Y'' THEN ',
'         UPDATE FRC_STORE_ORDER_FILE_DATA',
'            SET ERROR = ''Error: Invalid Store Provided AD Id. Store AD is must start with AD_''',
'          WHERE FILE_ID = vFILE_ID',
'            and UPPER(SUBSTR(LTRIM(RTRIM(EVENT_ID)),1,3)) != ''AD_''',
'         ;',
'      END IF;',
'',
'      --',
'      -- ',
'      vBOOKMARK := 35;',
