,p_process_name=>'PROCESS_FILE_KEY_VENDOR'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE ',
'  vFILE_ID VARCHAR2(25) := NULL;',
'  vERROR_MESSAGE VARCHAR2(2000):= ''SUCCESS'';',
'  vROW_CNT NUMBER := 0;',
'  vBANNER VARCHAR(3) := ''ACC'';',
'BEGIN',
'    --',
'    IF :P9_FILE_BROWSE IS NOT NULL THEN ',
'        --',
'        SELECT FILE_LOAD_LK_SEQ.NEXTVAL INTO vFILE_ID FROM DUAL;',
'        --',
'        INSERT INTO FRC_FILE_LOAD_LK_VW',
'        (',
'            BUSINESS_UNIT,',
'            FILE_ID      ,',
'            FILE_TYPE    ,',
'            FILE_NAME    ,',
'            FILE_LOAD_FLG,',
'            FILE_LOAD_DTM,',
'            CREATED_BY   ,',
'            CREATED_DTM ',
'        )',
'        VALUES ',
'        (',
'            vBANNER,',
'            vFILE_ID,',
'            :P9_FILE_TYPE, --KEY_VENDOR,MASTER_LIST,MINIMUM_PRESENTATION_VALUE,PAYMENT_DAY,SEASONAL_ITEMS,STORE_BUY_SELL,SEASONAL_ITEMS,STORE_COMM_DAYS,STORE_SPEC_PMT,STORE_VENDOR_COMM_DAYS',
'            SUBSTR(:P9_FILE_BROWSE, INSTR(:P9_FILE_BROWSE, ''/'') + 1),',
'            ''N'',',
'            SYSDATE,',
'            :APP_USER,',
'            SYSDATE',
'        );',
'        --  ',
'        FOR X IN (  select  line_number,',
'                            col001  AS BUSINESS_UNIT,',
'                            col002  AS STORE            ,',
'                            col003  AS VENDOR          ,',
'                            col004  AS LOWER_THRESHOLD_PERCENT       ,',
'                            col005  AS HIGHER_THRESHOLD_PERCENT      ',
'                          ',
'                    from apex_application_temp_files f, ',
'                         table( apex_data_parser.parse(',
'                                p_content                     => f.blob_content,',
'                                p_add_headers_row             => ''Y'',',
'                                p_max_rows                    => 50000,',
'                                p_store_profile_to_collection => ''FILE_PARSER_COLLECTION'',',
'                                p_file_name                   => f.filename ) ) p',
'                   where name = :P9_FILE_BROWSE',
'                     and line_number > 1',
'                     -- and REGEXP_LIKE(col001, ''^[[:digit:]]+$'')',
'                     -- and REGEXP_LIKE(col002, ''^[[:digit:]]+$'')',
'                 )',
'        LOOP',
'        --',
'            vROW_CNT := X.line_number;',
'            INSERT INTO FRC_KEY_VENDOR_STG_VW',
'            (',
'                FILE_ID                     ,',
'                BUSINESS_UNIT               ,',
'                STORE                       ,',
'                VENDOR                      ,',
'                LOWER_THRESHOLD_PERCENT     ,',
'                HIGHER_THRESHOLD_PERCENT    ,',
'                CREATED_BY                  ,',
'                CREATED_DTM                           ',
'            ) ',
'            VALUES',
'            (           ',
'                vFILE_ID                                               ,',
'                SUBSTR(LTRIM(RTRIM(X.BUSINESS_UNIT)),1,3)              ,',
'                SUBSTR(LTRIM(RTRIM(X.STORE)),1,4)                      ,',
'                SUBSTR(LTRIM(RTRIM(X.VENDOR)),1,50)                    ,',
'                TO_NUMBER(LTRIM(RTRIM(X.LOWER_THRESHOLD_PERCENT)))     ,',
'                TO_NUMBER(LTRIM(RTRIM(X.HIGHER_THRESHOLD_PERCENT)))    ,',
'                :APP_USER                                              ,',
'                SYSDATE ',
'            );  ',
'          --',
'        END LOOP;',
'        --',
'        DATA_LOAD.STG2KEY_VENDOR(vBANNER,vFILE_ID,vERROR_MESSAGE);        ',
'        --',
'    END IF;',
'EXCEPTION WHEN OTHERS THEN',
'    APEX_UTIL.SET_SESSION_STATE(''P9_ERROR_MESSAGE'', ''Error importing file '' || :P9_FILE_BROWSE || '' at line '' || vROW_CNT || '': '' || vERROR_MESSAGE);',
'    RAISE;',
'END;'))
,p_process_clob_language=>'PLSQL'
,p_process_error_message=>'&P9_ERROR_MESSAGE.'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(1238453903335665836)
,p_process_when=>'P9_FILE_TYPE'
,p_process_when_type=>'VAL_OF_ITEM_IN_COND_EQ_COND2'
,p_process_when2=>'KEY_VENDOR'
,p_process_success_message=>'The file has been imported'
);
