'DECLARE ',
'vALLOW_USER_LOGIN VARCHAR2(1);',
'vLOGIN_ALLOWED_FROM VARCHAR2(5);',
'vLOGIN_ALLOWED_TO VARCHAR2(5);',
'BEGIN',
'    FOR C IN ',
'    (',
'        SELECT ',
'        CASE ',
'            WHEN REPLACE(NVL(LOGIN_ALLOWED_FROM,''00:00''),'':'') <= REPLACE(NVL(LOGIN_ALLOWED_TO,''23:59''),'':'') AND TO_CHAR(SYSDATE,''HH24MI'') BETWEEN REPLACE(NVL(LOGIN_ALLOWED_FROM,''00:00''),'':'') and REPLACE(NVL(LOGIN_ALLOWED_TO,''23:59''),'':'') THEN ''Y'' ',
'            WHEN REPLACE(NVL(LOGIN_ALLOWED_FROM,''00:00''),'':'') > REPLACE(NVL(LOGIN_ALLOWED_TO,''23:59''),'':'') AND (TO_CHAR(SYSDATE,''HH24MI'') < REPLACE(NVL(LOGIN_ALLOWED_TO,''00:00''),'':'') OR TO_CHAR(SYSDATE,''HH24MI'') >= REPLACE(NVL(LOGIN_ALLOWED_FROM,''23:'
||'59''),'':'')) THEN ''Y'' ',
'            ELSE ''N'' ',
'        END AS ALLOW_USER_LOGIN,',
'        NVL(LOGIN_ALLOWED_FROM,''00:00'') AS LOGIN_ALLOWED_FROM,',
'        NVL(LOGIN_ALLOWED_TO,''23:59'') AS LOGIN_ALLOWED_TO',
'        INTO ',
'        vALLOW_USER_LOGIN,vLOGIN_ALLOWED_FROM,vLOGIN_ALLOWED_TO',
'        FROM FRC_SETTINGS',
'        WHERE USER_TYPE = FRC_UTILS.GET_USER_TYPE(upper(:P9999_USERNAME))',
'    )',
'    LOOP',
'        vALLOW_USER_LOGIN := C.ALLOW_USER_LOGIN;',
'        vLOGIN_ALLOWED_FROM := C.LOGIN_ALLOWED_FROM;',
'        vLOGIN_ALLOWED_TO := C.LOGIN_ALLOWED_TO;',
'    END LOOP;',
'',
'    IF vALLOW_USER_LOGIN IS NULL THEN',
'        APEX_ERROR.ADD_ERROR (',
'            p_message          => ''The user is not properly configured in Users table'',',
'            p_display_location => APEX_ERROR.C_INLINE_WITH_FIELD_AND_NOTIF,',
'            p_page_item_name   => ''P9999_USERNAME''',
'        );',
'    ELSIF vALLOW_USER_LOGIN = ''N'' THEN',
'        APEX_ERROR.ADD_ERROR (',
'            p_message          => ''Login is allowed between '' || vLOGIN_ALLOWED_FROM || '' and '' || vLOGIN_ALLOWED_TO || '' only'',',
'            p_display_location => APEX_ERROR.C_INLINE_WITH_FIELD_AND_NOTIF,',
'            p_page_item_name   => ''P9999_USERNAME''',
'        );',
'    ELSE',
'        apex_authentication.login(',
'            p_username => :P9999_USERNAME,',
'            p_password => :P9999_PASSWORD );',
'    END IF;',
'',
'END;'))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
