'vLOGIN_ALLOWED_FROM VARCHAR2(5);',
'vLOGIN_ALLOWED_TO VARCHAR2(5);',
'BEGIN',
'    FOR C IN ',
'    (',
'        SELECT ',
'        CASE ',
'            WHEN REPLACE(NVL(LOGIN_ALLOWED_FROM,''00:00''),'':'') <= REPLACE(NVL(LOGIN_ALLOWED_TO,''23:59''),'':'') AND TO_CHAR(SYSDATE,''HH24MI'') BETWEEN REPLACE(NVL(LOGIN_ALLOWED_FROM,''00:00''),'':'') and REPLACE(NVL(LOGIN_ALLOWED_TO,''23:59''),'':'') THEN ''Y'' ',
