||'59''),'':'')) THEN ''Y'' ',
'            ELSE ''N'' ',
'        END AS ALLOW_USER_LOGIN,',
'        NVL(LOGIN_ALLOWED_FROM,''00:00'') AS LOGIN_ALLOWED_FROM,',
'        NVL(LOGIN_ALLOWED_TO,''23:59'') AS LOGIN_ALLOWED_TO',
'        INTO ',
'        vALLOW_USER_LOGIN,vLOGIN_ALLOWED_FROM,vLOGIN_ALLOWED_TO',
'        FROM FRC_SETTINGS',
'        WHERE USER_TYPE = FRC_UTILS.GET_USER_TYPE(upper(:P9999_USERNAME))',
'    )',
'    LOOP',
'        vALLOW_USER_LOGIN := C.ALLOW_USER_LOGIN;',
'        vLOGIN_ALLOWED_FROM := C.LOGIN_ALLOWED_FROM;',
'        vLOGIN_ALLOWED_TO := C.LOGIN_ALLOWED_TO;',
'    END LOOP;',
'',
'    IF vALLOW_USER_LOGIN IS NULL THEN',
'        APEX_ERROR.ADD_ERROR (',
'            p_message          => ''The user is not properly configured in Users table'',',
'            p_display_location => APEX_ERROR.C_INLINE_WITH_FIELD_AND_NOTIF,',
'            p_page_item_name   => ''P9999_USERNAME''',
'        );',
'    ELSIF vALLOW_USER_LOGIN = ''N'' THEN',
'        APEX_ERROR.ADD_ERROR (',
'            p_message          => ''Login is allowed between '' || vLOGIN_ALLOWED_FROM || '' and '' || vLOGIN_ALLOWED_TO || '' only'',',
